import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import UploadControl from "./UploadControl";
import UploadWrapper from "../styled/uploadStyled/UploadWrapper";
import UploaderStyled from "../styled/uploadStyled/UploaderStyled";

const UploadTest = () => {
  const initialValues = {
    caption: "",
    description: "",
    video: "",
  };

  const validationSchema = Yup.object({
    caption: Yup.string().required("required"),
    description: Yup.string().required("required"),
  });

  const onSubmit = (values) => {
    console.log(values);
  };

  return (
    <UploadWrapper>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}
        validateOnMount
      >
        {(formik) => (
          <Form>
            <UploaderStyled>
              <UploadControl control="input" type="file" name="video" />
            </UploaderStyled>
            <UploadControl
              control="input"
              type="text"
              label="Caption"
              name="caption"
            />
            <UploadControl
              control="textarea"
              label="description"
              name="description"
            />
            <button
              type="submit"
              disabled={!formik.isValid || formik.isSubmmiting}
            >
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </UploadWrapper>
  );
};

export default UploadTest;
