import React, { useEffect, useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import UploadControl from "./UploadControl";
import UploadWrapper from "../styled/uploadStyled/UploadWrapper";
import UploaderStyled from "../styled/uploadStyled/UploaderStyled";
import ButtonWrapper from "../styled/uploadStyled/ButtonWrapper";
import UploadButtonStyled from "../styled/uploadStyled/UploadButtonStyled";
import PostFormStyled from "../styled/uploadStyled/PostFormStyled";
import InputWrapper from "../styled/uploadStyled/InputWrapper";

import { useSelector, useDispatch } from "react-redux";
import { uploadPost } from "../../redux/module/upload";
import axios from "axios";

const UploadTest = () => {
  const dispatch = useDispatch();
  const initialValues = {
    id: 1,
    user_id: "beomsu",
    tags: "",
    description: "",
    file: "",
  };

  const validationSchema = Yup.object({
    tags: Yup.string().required("required"),
    description: Yup.string().required("required"),
  });

  const onSubmit = (values) => {
    const formData = new FormData();
    formData.append("id", 1);
    formData.append("user_id", "beomsu");
    formData.append("tags", values.tags);
    formData.append("description", values.description);
    formData.append("file", values.file);
    axios({
      method: "post",
      url: "http://localhost:8080/api/v1/posts",
      data: formData,
      headers: {
        "Content-Type": "multipart/form-data",
        Authorization: localStorage.getItem("access_token"),
      },
    });
    console.log(formData);
  };

  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={onSubmit}
      validateOnMount
    >
      {(formik) => (
        <Form>
          <UploadWrapper>
            <PostFormStyled>
              <h3>Upload Your Day</h3>
              <UploaderStyled>
                <input
                  type="file"
                  label="file"
                  onChange={(e) =>
                    formik.setFieldValue("file", e.target.files[0])
                  }
                />
              </UploaderStyled>
            </PostFormStyled>
            <InputWrapper>
              <UploadControl
                control="input"
                type="text"
                label="tags"
                name="tags"
              />
              <UploadControl
                control="textarea"
                label="description"
                name="description"
              />
            </InputWrapper>
          </UploadWrapper>
          <ButtonWrapper>
            <UploadButtonStyled
              type="submit"
              disabled={!formik.isValid || formik.isSubmmiting}
            >
              upload
            </UploadButtonStyled>
          </ButtonWrapper>
        </Form>
      )}
    </Formik>
  );
};

export default UploadTest;
